insert into bookInfo (ISBN, bookName, classID, publisher, author, introduce, price, pubDatetime, coverPath, inDatetime ) values('7-115-12092-7','Visual foxpro数据库开发关键技术与实例应用',3,'人民邮电出版社','周桓、张雨、王国辉等','本书从Visual FoxPro用户经常遇到的问题入手，<br/>结合应用实例的讲解，帮助读者快速掌握使用Visual FoxPro开发数据库管理系统的关键技术、方法和技巧，从而开发出适合企业应用的数据库系统。<br/>   全书共分为13章，内容包括如何使用代码设计SQL Server数据库、如何连接数据库、数据集与数据访问组件应用技巧、数据输入输出技术、常用控制组件应用技术、SQL数据查询技术、<br/>设计报表和图表、软件与数据库安全、计算机外围硬件程序设计、数据库典型程序设计、多层结构程序开发设计、SQL Server 2000开发设计、使用InstallShield制作安装程序。<br/>   本书配套光盘中提供了书中应用实例的源程序代码，所有源程序均在Windows 98或Windows XP下调试通过，确保能够正常运行。本书内容精练、重点突出，适合Delphi程序开发人员阅<br/>读和参考，也可供大中专院校计算机相关专业的师生学习。',40,'','cover\delphisjkkfgjjsyslyy.jpg','');
